import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import jwt_decode from "jwt-decode";
import logo from "../90278.png";

const Navbar = () => {
  const history = useNavigate();
  const [name, setName] = useState("");
  const [id, setId] = useState("");
  const [role, setRole] = useState("");

  useEffect(() => {
    getUser();
  }, []);

  const Logout = async () => {
    try {
      await axios.delete("http://localhost:5000/logout");
      history("/");
    } catch (error) {
      console.log(error);
    }
  };

  const gantiPassword = async () => {
    history(`/gantiPassword/` + id);
  };

  const getUser = async () => {
    const res = await axios.get("http://localhost:5000/token");
    const decode = jwt_decode(res.data.accessToken);
    setName(decode.name);
    setId(decode.userId);
    setRole(decode.role);
  };

  function editUser() {
    history(`/add-User/` + id);
  }

  function showRoles() {
    if (role === "admin") {
      return (
        <div>
          <a href="/roles" className="navbar-item">
            Roles
          </a>
          <a href="/customer" className="navbar-item">
            Customer
          </a>
        </div>
      );
    }
  }
  return (
    <nav
      className="navbar is-light"
      role="navigation"
      aria-label="main navigation"
    >
      <div className="container">
        <div className="navbar-brand">
          <a className="navbar-item" href="/home">
            <img src={logo} width="112" height="28" alt="logo" />
          </a>

          <a
            href="/home"
            role="button"
            className="navbar-burger burger"
            aria-label="menu"
            aria-expanded="false"
            data-target="navbarBasicExample"
          >
            <span aria-hidden="true"></span>
            <span aria-hidden="true"></span>
            <span aria-hidden="true"></span>
          </a>
        </div>

        <div id="navbarBasicExample" className="navbar-menu">
          <div className="navbar-start">
            <a href="/home" className="navbar-item">
              Home
            </a>
            {showRoles()}
            <a href="/produk" className="navbar-item">
              Produk
            </a>
          </div>

          <div className="navbar-end">
            <div className="navbar-item">
              <div className="buttons">
                <button onClick={editUser} className="button is-dark">
                  {name},{role}
                </button>
                <button onClick={gantiPassword} className="button is-success">
                  Ganti Password
                </button>
                <button onClick={Logout} className="button is-danger">
                  Log Out
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
