import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import jwt_decode from "jwt-decode";
import swal from "sweetalert";

const CreateUser = () => {
  let params = useParams();
  const [id, setId] = useState(params.id);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const history = useNavigate();
  const [token, setToken] = useState("");
  const [expired, setExpired] = useState("");
  const axiosJwt = axios.create();
  const [roles, setRoles] = useState([]);
  const [role, setRole] = useState("");
  const [roleToken, setRoleToken] = useState("");

  useEffect(() => {
    refreshToken();
    getRoles();
    cekId();
    // eslint-disable-next-line
  }, []);

  const refreshToken = async () => {
    try {
      const response = await axios.get("http://localhost:5000/token");
      setToken(response.data.accessToken);
      const decode = jwt_decode(response.data.accessToken);
      // setName(decode.name);
      // setUserId(decode.userId);
      setRoleToken(decode.role);
      setExpired(decode.exp);
      if (decode.role !== "admin") {
        if (id != decode.userId) {
          history("/home");
        }
      }
    } catch (error) {
      if (error.response) {
        history("/");
      }
    }
  };

  axiosJwt.interceptors.request.use(
    async (config) => {
      const currentDate = new Date();
      if (expired * 1000 < currentDate.getTime()) {
        const response = await axios.get("http://localhost:5000/token");
        config.headers.Authorization = `Bearer ${response.data.accessToken}`;
        setToken(response.data.accessToken);
        const decode = jwt_decode(response.data.accessToken);
        // setName(decode.name);
        // setUserId(decode.userId);
        setRoleToken(decode.role);
        setExpired(decode.exp);
      }
      return config;
    },
    (error) => {
      return Promise.reject(error);
    }
  );

  const cekId = async () => {
    if (id === "_add") {
      return;
    } else {
      const res = await axiosJwt.get("http://localhost:5000/users/" + id, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      let User = res.data;
      setId(User.id);
      setName(User.name);
      setEmail(User.email);
      setPassword(User.password);
      setRole(User.role);
    }
  };

  const getRoles = async () => {
    const response = await axios.get("http://localhost:5000/roles");
    setRoles(response.data);
    setRole(response.data[0].role);
  };

  const saveOrUpdateUser = async (e) => {
    e.preventDefault();

    if (id === "_add") {
      let User = {
        name: name,
        email: email,
        password: password,
        role: role,
      };
      await axios
        .post("http://localhost:5000/adduser", User, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        .then((res) => {
          swal(res.data.msg);
          history("/home");
        });
    } else {
      let User2 = {
        name: name,
        email: email,
        role: role,
      };
      await axios
        .put("http://localhost:5000/users/" + id, User2, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        .then((res) => {
          swal(res.data.msg);
          history("/home");
        });
    }
  };

  function cancel() {
    history("/home");
  }

  function getTitle() {
    if (id === "_add") {
      return <h3 className="text-center">Add User</h3>;
    } else {
      return <h3 className="text-center">Update User</h3>;
    }
  }

  function addPassword() {
    if (id === "_add") {
      return (
        <div className="form-group">
          <label>Password</label>
          <input
            placeholder="*****"
            name="alamat"
            type="password"
            className="form-control"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
      );
    }
  }

  function editRole() {
    if (roleToken === "admin") {
      return (
        <div className="form-group">
          <label>Select Roles</label>
          <div className="controls">
            <select
              className="form-control"
              name="roles"
              id="roles"
              onChange={(e) => setRole(e.target.value)}
              value={role}
            >
              {roles.map((role) => (
                <option key={role.id}>{role.role}</option>
              ))}
            </select>
          </div>
        </div>
      );
    }
  }

  return (
    <div>
      <br></br>
      <div className="container">
        <div className="row">
          <div className="card col-md-6 offset-md-3 offset-md-3">
            <br></br>

            {getTitle()}

            <div className="card-body">
              <form>
                <div className="form-group">
                  <label>Name </label>
                  <input
                    placeholder="Name"
                    name="name"
                    className="form-control"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                  />
                </div>
                <div className="form-group">
                  <label>Email</label>
                  <input
                    placeholder="Email"
                    name="email"
                    className="form-control"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </div>
                {editRole()}
                {addPassword()}
                <br></br>
                <button className="btn btn-success" onClick={saveOrUpdateUser}>
                  Simpan
                </button>
                <button className="btn btn-danger ml-4" onClick={cancel}>
                  Batal
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CreateUser;
